function selectEvent(checked,eventId,defaultLimitUp,defaultUnitId){var limitUpField=document.getElementById("edit-limit-up"+eventId);var unitIdField=document.getElementById("edit-unit-id"+eventId);if(limitUpField){limitUpField.readOnly=!checked;limitUpField.value=checked?defaultLimitUp:""}if(unitIdField)unitIdField.value=defaultUnitId;if((eventId==1||eventId==3||eventId==201||eventId==200)&&!checked){var anyIssueField=getEventIdField(9);if(anyIssueField)anyIssueField.checked=false}else if(eventId==
9)checkDeviceIssueEvents()}function changeLimitUp(eventId,field){var eventIdField=getEventIdField(eventId);field.readOnly=!eventIdField.checked}function checkDeviceIssueEvents(){var field=getEventIdField(9);checkDeviceIssueEvent(field.checked,1,2,2);checkDeviceIssueEvent(field.checked,3,2,2);checkDeviceIssueEvent(field.checked,201);checkDeviceIssueEvent(field.checked,200)}
function checkDeviceIssueEvent(checked,eventId,defaultLimitUp,defaultUnitId){var eventIdField=getEventIdField(eventId);eventIdField.checked=checked;selectEvent(checked,eventId,defaultLimitUp,defaultUnitId)}function getEventIdField(eventId){return document.getElementById("edit-event-id"+eventId)}
function showDeviceIssueEvents(show){var issuesDiv=document.getElementById("issues");var showDetailsLink=document.getElementById("showDetails");var hideDetailsLink=document.getElementById("hideDetails");if(show){issuesDiv.style.display="";showDetailsLink.style.display="none";hideDetailsLink.style.display=""}else{issuesDiv.style.display="none";showDetailsLink.style.display="";hideDetailsLink.style.display="none"}};